/**
 * @name xue.calendar.min.js
 * @description 日历功能，包括js和css文件
 * 
 * @module 
 * @submodule 
 * @main 
 * @class 
 * @constructor 
 * @static 
 */

xue.calendar = xue.calendar || function( expr, opt ){
    if(!expr){ return; }
    var csspath = 'http://css04.xesimg.com/ui.calendar.css',
        jspath  = 'http://js04.xesimg.com/xue.ui.calendar.js';

    if(xue.load.isExist(jspath)){
        setTimeout(function(){
            if($(expr).length > 0){
                $(expr).calendar(opt);
            }
        }, 200);
    }else{
        xue.load(csspath);
        xue.load(jspath,function(){
            if($(expr).length > 0){
                $(expr).calendar(opt);
            }
        });
    }
};