/**
 * 弹出提示
 * @param  {string}   msg   弹窗内要显示的内容
 * @param  {Function} fn    点击确定按钮的回调函数
 * @return {[type]}         [description]
 */
ui.confirm = ui.confirm || function(msg, fn1, fn2){
    // var arg = arguments, 
    //     tp = null, con = false, func = true;
    // $.each(arg, function(k, v){
    //     tp = typeof v;
    //     msg    = tp === 'string'   ? v : msg ;
    //     fn     = tp === 'function' ? v : fn ;
    // });
    
    if(!msg || msg === ''){ return; }

    var ok = fn1 || true,
        no = fn2 || true;

    xue.dialog({
        id      : 'confirm',
        title   : false,
        lock    : true,
        content : '<div style="text-align:center">' + msg + '</div>',
        ok      : function(){
            if(typeof ok === 'function'){
                ok();
            }
            xue.dialog('confirm').close();
        },
        cancel  : function(){
            if(typeof no === 'function'){
                no();
            }
            xue.dialog('confirm').close();  
        }
    });
};

xue.extend('confirm', ui.confirm);

